<template>
  <div class="container">
    <img src="data:image/jpeg;base64," alt="">
    <p class="card-text">
     {{randomJoke}}
    </p>
    <label name="category" for="category">What is your favorite joke category</label>
    <input class="form-control mb-2" type="text" v-model="category">
    <button @click="handleClick()">Get new joke</button>
  </div>
</template>

<script>
import { mapActions, mapGetters, mapMutations } from 'vuex'
import  store  from './../store'

export default {
  data() {
    return {
      category:''
    }
  },
  name: 'Chuck',
  computed: {
    ...mapGetters({
      randomJoke: 'getRandomJoke'
    })
  },
  methods:{
    ...mapActions(['getRandomJoke','getCategoryJoke']),
    
    handleClick(category) {
      category = this.category
      this.getCategoryJoke(category)
    },
  },
  // created() {
  //   this.getRandomJoke()
  // }
  beforeRouteEnter(to, from, next) {
    store.dispatch('getRandomJoke', next);

  }
}
</script>
